function isPalindrome(str) {
  if (str.toLowerCase().split("").reverse().join("") === str.toLowerCase()) {
    console.log(`${str} is a palindrome`);
  } else {
    console.log(`${str} is not a palindrome`);
  }
}

isPalindrome("Racecar");
isPalindrome("Hello");

function countChar(str, char) {
  return str.toLowerCase().split("").filter((ch) => ch === char).length;
}

console.log(countChar("Ababagalamaga", "a"));
console.log(
  countChar("Count the number of occurrences of a specific character.", " ")
);

function reverseString(str, delimiter) {
return str.split(delimiter).reverse().join(delimiter)  
}

console.log(reverseString("Racecar", ""))
console.log(reverseString("one two three", " "))
