function isWeekend(date) {
  const day = date.getDay(); // 0 = Sunday, 6 = Saturday
  return day === 0 || day === 6;
}

const someDate = new Date("2025-05-24"); // субота
console.log(isWeekend(someDate) ? "Вихідний день" : "Робочий день");

const today = new Date();
const newYear = new Date(today.getFullYear() + 1, 0, 1); 

const diffInMs = newYear - today;
const diffInDays = Math.ceil(diffInMs / (1000 * 60 * 60 * 24));

console.log(`До Нового року залишилось: ${diffInDays} днів`);

const eventDate = new Date("2025-06-01T00:00:00");

function countdown() {
  const now = new Date();
  const diff = eventDate - now;

  if (diff <= 0) {
    console.log("Подія вже настала!");
    clearInterval(timer);
    return;
  }

  const days = Math.floor(diff / (1000 * 60 * 60 * 24));
  const hours = Math.floor((diff / (1000 * 60 * 60)) % 24);
  const minutes = Math.floor((diff / (1000 * 60)) % 60);
  const seconds = Math.floor((diff / 1000) % 60);

  console.log(`Залишилось: ${days}д ${hours}г ${minutes}хв ${seconds}с`);
}

const timer = setInterval(countdown, 1000);

function updateClock() {
    const now = new Date();
    const time = now.toLocaleTimeString();
    document.getElementById('clock').textContent = time;
}

setInterval(updateClock, 1000);
updateClock(); // одразу показати перше значення

const lastVisit = localStorage.getItem("lastVisit");

if (lastVisit) {
  console.log(
    `Ваш останній візит був: ${new Date(lastVisit).toLocaleString("uk-UA")}`
  );
} else {
  console.log("Вітаємо! Це ваш перший візит.");
}

localStorage.setItem("lastVisit", new Date());
